package program17;

public class CommonArrElement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int[] arr1 = {18,24,25};
		  int[] arr2 = {75,98,24,43,18};

		  for(int i = 0; i < arr1.length; i++){
		   for(int j = 0; j < arr2.length; j++){
		    if(arr1[i] == arr2[j]){
		     System.out.println(arr1[i]);
		     break;
		    }
	}
}
	}

}
